VideoGamesModal videoGamesModalFromJson(String str) => VideoGamesModal.fromJson(json.decode(str));
String videoGamesModalToJson(VideoGamesModal data) => json.encode(data.toJson());
class VideoGamesModal {
  VideoGamesModal({
      num? responseCode, 
      List<Results>? results,}){
    _responseCode = responseCode;
    _results = results;
}

  VideoGamesModal.fromJson(dynamic json) {
    _responseCode = json['response_code'];
    if (json['results'] != null) {
      _results = [];
      json['results'].forEach((v) {
        _results?.add(Results.fromJson(v));
      });
    }
  }
  num? _responseCode;
  List<Results>? _results;
VideoGamesModal copyWith({  num? responseCode,
  List<Results>? results,
}) => VideoGamesModal(  responseCode: responseCode ?? _responseCode,
  results: results ?? _results,
);
  num? get responseCode => _responseCode;
  List<Results>? get results => _results;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['response_code'] = _responseCode;
    if (_results != null) {
      map['results'] = _results?.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

